package types;

public class operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=2;      
		int y=++x;         //Pre-increment
		
		int r=5;
		r+=6;       
		System.out.println(r);
		
		System.out.println();
		
		
		
//		int x=2;        
//		int y=x++;        Post increment
//		
		
		System.out.println(x);
		System.out.println(y);
	}

}
